import * as React from 'react';
import {useState} from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, SafeAreaView, View, Text, TouchableOpacity, TextInput, Button, ImageBackground,Switch } from 'react-native';
import { IconButton, Colors } from 'react-native-paper';

export default function Temperature({ navigation }) {

  const pressHandler = () => {
    navigation.goBack();
  }

  const Light = () => {
      navigation.replace('Light')
  }

  const AirQuality = () => {
      navigation.replace('AirQuality')
  }

  const Home = () => {
    navigation.replace('Home')
  }

  const App = () => {
    const [isEnabled, setIsEnabled] = useState(false);
    const toggleSwitch = () => setIsEnabled(previousState => !previousState);
  }

  return (
    <>
    
     <StatusBar style="light" />
      <View>
          <Text style={styles.text}>Temperature</Text>
      </View>

      <View style={styles.container}>
        <Switch
          trackColor={{ false: "#767577", true: "#81b0ff" }}
          thumbColor={isEnabled ? "#f5dd4b" : "#f4f3f4"}
          ios_backgroundColor="#3e3e3e"
          onValueChange={toggleSwitch}
          value={isEnabled}
        />
      </View>

     <View style={{flex: 1}}>
       <View style={{flexDirection:"row",  height: 50, bottom: -480, left: 20}}>
         <View style={{flex:1}}>
           <IconButton icon="brightness-6" size={50} onPress={Light}/>
         </View>
         <View style={{flex:1}}>
           <IconButton icon="air-filter" size={50} onPress={AirQuality}/>
         </View>
         <View style={{flex:1}}>
           <IconButton icon="home" size={50} onPress={Home}/>
         </View>
       </View>
     </View>
  </>
  );
}

const styles = StyleSheet.create({
  
  text: {
    textAlign: "center",
    fontFamily: "Cochin",
    fontWeight: "bold",
    color: '#3A6238',
    fontSize: 30,
    lineHeight: 40,

  },

  container: {
    flex: 1,
    
    alignItems: "center",
    justifyContent: "center"
  }
});
