import * as React from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, SafeAreaView, View, Text, Image, TouchableOpacity, TextInput, Button, ImageBackground } from 'react-native';
import Constants from "expo-constants";
import { IconButton, Colors } from 'react-native-paper'; // https://materialdesignicons.com

export default function Home({ navigation }) {

    const Temperature = () => {
        navigation.navigate('Temperature')
    }

    const Light = () => {
        navigation.navigate('Light')
    }

    const AirQuality = () => {
        navigation.navigate('AirQuality')
    }

  return (
    <>
      
      <StatusBar style="light" />
      <View style={styles.content}>
          <Text style={styles.text}>LEMON TREE</Text>
      </View>
      <View style={{width: 200, height: 200, }}>
        <Image style={{height: '250%', width: "195%"}} source={require('../assets/lemon_tree_5.jpg')}></Image>
        {/* //<Image source={{ uri:'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQL5-1HLkiY4AEM29gbKS-rs_oO2JPv2V6bCw&usqp=CAU',}} /> */}
      </View>
      <View style={{flex: 1}}>
        <View style={{flexDirection:"row", height: 50, bottom: -300, left: 20, }}>
          <View style={{flex:1}}>
            <IconButton icon="brightness-6" size={50} onPress={Light}/>
          </View>
          <View style={{flex:1}}>
            <IconButton icon="air-filter" size={50} onPress={AirQuality}/>
          </View>
          <View style={{flex:1}}>
            <IconButton icon="thermometer-lines" size={50} onPress={Temperature}/>
          </View>
        </View>
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#7EB37A",
    paddingTop: Constants.statusBarHeight,
    opacity: 1,
  },
  text: {
    backgroundColor: '#D9E8D8',
    textAlign: "center",
    fontFamily: "Cochin",
    fontWeight: "bold",
    color: '#E6E2DD',
    fontSize: 20,
    lineHeight: 20,

  },
  content: {
    paddingHorizontal: 40,
    
  },


});
