import * as React from 'react';
import { StyleSheet, SafeAreaView, View, Text, TouchableOpacity, TextInput, Button, ImageBackground } from 'react-native';
import Constants from "expo-constants";
import { IconButton, Colors } from 'react-native-paper'; // https://materialdesignicons.com

export default function Home({ navigation }) {

    const Temperature = () => {
        navigation.navigate('Temperature')
    }

    const Light = () => {
        navigation.navigate('Light')
    }

    const AirQuality = () => {
        navigation.navigate('AirQuality')
    }

  return (
    <>
      <View style={{textAlign: 'center', top: 10, }}>
        *PLANT NAME*
      </View>
      <View style={{width: 200, height: 200, }}>
        <Image source="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQeWK2zPriYtrdqEp_4HdfMP60rT5Fm0Z3mDg&usqp=CAU"/>
      </View>
      <View style={{flex: 1}}>
        <View style={{flexDirection:"row", height: 50, position: 'absolute', bottom: 30, left: 50, }}>
          <View style={{flex:1}}>
            <IconButton icon="thermometer-lines" size={50} onPress={Temperature}/>
          </View>
          <View style={{flex:1}}>
            <IconButton icon="brightness-6" size={50} onPress={Light}/>
          </View>
          <View style={{flex:1}}>
            <IconButton icon="air-filter" size={50} onPress={AirQuality}/>
          </View>
        </View>
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#000",
    paddingTop: Constants.statusBarHeight,
    opacity: 1,
  },
});
