import * as React from 'react';
import {useState} from 'react';
import { WebView } from 'react-native-webview';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, ScrollView, SafeAreaView, View, Text, TouchableOpacity, TextInput, Button, ImageBackground, Switch } from 'react-native';
import { IconButton, Colors } from 'react-native-paper';

export default function AirQuality({ navigation }) {

  const pressHandler = () => {
    navigation.goBack();
  }

  const Temperature = () => {
      navigation.replace('Temperature')
  }

  const Light = () => {
      navigation.replace('Light')
  }

  const Home = () => {
    navigation.replace('Home')
  }

  const [isEnabled, setIsEnabled] = useState(false);
  const toggleSwitch = () => setIsEnabled(previousState => !previousState);

  return (
    <>
      <StatusBar style="light" />
      <View>
          <Text style={styles.main_text}>Humidity</Text>
      </View>

      <WebView source={{ uri: 'https://thingspeak.com/channels/1548631/charts/5?bgcolor=%23ffffff&color=%23d62020&dynamic=true&results=60&type=line&update=15' }} 
      scalesPageToFit={true}
      style={{ width: '230%', marginTop: 10,marginBottom: 20}}
      />

      <View style={{flex: 0.3}}>
        <View style={{flexDirection:"row",  height: 40, }}>
          <View style={{flex:1}}>
            <Text style={styles.text}>Actual value</Text>
          </View>

          <View style={{flex:1}}>
            <Text style={styles.t_text}>67 %</Text>
          </View>
        </View>
      </View>
      
      <View style={{flex: 0.3}}>
        <View style={{flexDirection:"row",  height: 50}}>
          <View style={{flex:1}}>
            <Text style={styles.text}>Manual Control</Text>
          </View>

          <View style={{flex:1}} style={styles.container}>
          <Switch
            trackColor={{ false: "#767577", true: "#3A6238" }}
            thumbColor={isEnabled ? "#7EB37A" : "#f4f3f4"}
            ios_backgroundColor="#3e3e3e"
            onValueChange={toggleSwitch}
            value={isEnabled}
          />
          </View>
        </View>
      </View>

      <View style={{flex: 0.3}}>
        <View style={{flexDirection:"row",  height: 40}}>
          <View style={{flex:1}}>
            <Text style={styles.text}>Setpoint</Text>
          </View>

          <View style={{flex:1}}>
            <Text style={styles.t_text}>54 %</Text>
          </View>
        </View>
      </View>

      <View style={{flex: 0.5}}>
        <View style={{flexDirection:"row", bottom: -30, left: 20}}>
          <View style={{flex:1}}>
            <IconButton icon="thermometer-lines" size={50} onPress={Temperature}/>
          </View>
          <View style={{flex:1}}>
            <IconButton icon="brightness-6" size={50} onPress={Light}/>
          </View>
          <View style={{flex:1}}>
           <IconButton icon="home" size={50} onPress={Home}/>
         </View>
        </View>
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  
  main_text: {
    textAlign: "center",
    fontFamily: "Cochin",
    fontWeight: "bold",
    color: '#3A6238',
    fontSize: 30,
    lineHeight: 40,

  },

  text: {
    textAlign: "center",
    fontFamily: "Cochin",
    fontWeight: "bold",
    color: '#3A6238',
    fontSize: 25,
    lineHeight: 35,

  },

  t_text: {
    textAlign: "center",
    fontFamily: "Cochin",
    color: '#3A6238',
    fontSize: 25,
    lineHeight: 35,
    backgroundColor: "#d9e8d8"
  },

  container: {
    flex: 1,
    alignItems: "center",
  },


});
