import React from "react";
import { StyleSheet, SafeAreaView, View, Text, TouchableOpacity, TextInput, ImageBackground, Button, Image } from "react-native";
import { StatusBar } from "expo-status-bar";
import { FontAwesome5 } from "@expo/vector-icons";
import Constants from "expo-constants";

export default function Login({navigation}) {

  const pressHandler = () => {
  //   navigation.replace('Home')
    navigation.navigate('Home')
  }

  // const image = { uri: "https://i.pinimg.com/736x/dd/3c/3e/dd3c3e14b253709e774d9c5bf0155a7a.jpg"};

  return (
    <SafeAreaView style={styles.container}>
      <ImageBackground source={require('../assets/jungle2.jpg')} style={styles.image}>
      <StatusBar style="light" />
      <View style={styles.content}>
        <View style={styles.textWrapper}>
          <Text style={styles.hiText}>VESNA</Text>
          <Text style={styles.userText}>Versatile Simulator for Near-zero Emissions Agriculture</Text>
          <Text style={styles.userText}>UIAM - STU BA</Text>
        </View>
        <View style={styles.emailform}>
          <TextInput
            style={[styles.input, styles.inputUsername]}
            placeholder="E-mail adress"
            placeholderTextColor="#fff"
          />
        </View>
        <View style={styles.form}>
          <FontAwesome5 name="lock" style={styles.iconLock} />
          <TextInput
            style={styles.inputPassword}
            keyboardType="numeric"
            secureTextEntry={true}
            autoFocus={true}
            placeholder="Password"
            placeholderTextColor="#929292"
          />
          <TouchableOpacity style={styles.buttonLogin}>
            <Text style={styles.buttonLoginText} onPress={pressHandler}>LOG IN</Text>
          </TouchableOpacity>
        </View>
        <View style={styles.action1}>
          <TouchableOpacity>
            <Text style={styles.userText}>Forgot possword...</Text>
          </TouchableOpacity>
        </View>
        <View style={styles.action2}>
          <TouchableOpacity>
            <Text style={styles.userText}>No account? Register here!</Text>
          </TouchableOpacity>
        </View>
      </View>
      </ImageBackground>
    </SafeAreaView>

  );
}

const TEXT = {
  textAlign: "center",
  fontFamily: "Cochin",
  fontSize: 100,
  fontWeight: "bold",
  color: '#E6E2DD',
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#000",
    paddingTop: Constants.statusBarHeight,
    opacity: 1,
  },
  content: {
    paddingHorizontal: 30,
  },
  textWrapper: {
    marginTop: 60,
    marginBottom: 30,
  },
  hiText: {
    ...TEXT,
    fontSize: 36,
    lineHeight: 50,
    fontWeight: "bold",
  },
  userText: {
    ...TEXT,
    fontSize: 14,
    lineHeight: 30,
  },
  form: {
    marginBottom: 30,
  },
  emailform: {
    marginBottom: 5,
  },
  iconLock: {
    color: "#929292",
    position: "absolute",
    fontSize: 16,
    top: 22,
    left: 22,
    zIndex: 10,
  },
  inputPassword: {
    height: 60,
    borderRadius: 30,
    paddingHorizontal: 30,
    fontSize: 20,
    color: "#929292",
    backgroundColor: "#fff",
    textAlign: "center",
    textAlignVertical: "center",
    marginTop: 1,
  },
  buttonLogin: {
    width: 40,
        // height: "7%",
    height: 50,
    borderRadius: 25,
    backgroundColor: "#E6E2DD",
    justifyContent: "center",
    marginTop: 15,
  },
  buttonLoginText: {
    textAlign: "center",
    fontSize: 18,
    fontFamily: "Cochin",
  },
  action1: {
    textAlign: "center",
    textAlignVertical: "center",
    marginTop: 20,
  },
  action2: {
    textAlign: "center",
    textAlignVertical: "center",
    marginTop: 10,
  },
  input: {
    borderWidth: 1,
    borderColor: "#cdcdcf",
    color: "#333333",
    fontSize: 18,
    height: 44,
    textAlign: "center",
    paddingHorizontal: 15,
  },
  inputUsername: {
    borderBottomWidth: 1,
    borderTopLeftRadius: 3,
    borderTopRightRadius: 3,
  },
  image: {
    flex: 1,
    justifyContent: "center",
    justifyContent: 'center'
    // overflow: "hidden",
  },
});
