import * as React from 'react';
import {useState, useEffect} from 'react';
import { WebView } from 'react-native-webview';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, ScrollView, SafeAreaView, View, Text, TouchableOpacity, TextInput, Button, ImageBackground, Switch, FlatList } from 'react-native';
import { IconButton, Colors } from 'react-native-paper';

export default function AirQuality({ navigation }) {

  const pressHandler = () => {
    navigation.goBack();
  }

  const Temperature = () => {
      navigation.replace('Temperature')
  }

  const Light = () => {
      navigation.replace('Light')
  }

  const Home = () => {
    navigation.replace('Home')
  }

  // Switch for on/off manual control
  const [isEnabled, setIsEnabled] = useState(false);
  const toggleSwitch = () => setIsEnabled(previousState => !previousState);

  // Switch for on/off actuator
  const [isEnabled1, setIsEnabled1] = useState(false);
  const toggleSwitch1 = () => {
    setIsEnabled1(previousState => !previousState)
  } 

  const [data, setData] = useState([]);
    useEffect(() => {
      // (1) define within effect callback scope
      const fetchData = async () => {
        try {
          const res = await fetch('https://thingspeak.com/channels/1548631/fields/5.json?results=1');
          const json = await res.json();
          setData(json);
        } catch (error) {
          console.log(error);
        }
      };
        
      const id = setInterval(() => {
        fetchData(); // <-- (3) invoke in interval callback
      }, 6000);
    
      fetchData(); // <-- (2) invoke on mount
    
      return () => clearInterval(id);
    }, [])

    // Manual on/off
    try {
      fetch('https://api.thingspeak.com/update.json' , {
       method: 'POST',
       headers: {
         Accept: 'application/json',
         'Content-Type': 'application/json',
       },
       body: JSON.stringify(
         {
           "api_key": "87FQ51LBR379FTF3",
          //  Livka channel F8RX7M2SU9EY8R44
           "field2": isEnabled ? '1' : '0',
         }
       )
     });
       
    } catch(error) {
    console.log(error);
    }

    // Actuator on/off
    try {
      fetch('https://api.thingspeak.com/update.json' , {
       method: 'POST',
       headers: {
         Accept: 'application/json',
         'Content-Type': 'application/json',
       },
       body: JSON.stringify(
         {
           "api_key": "87FQ51LBR379FTF3",
          //  Livka channel F8RX7M2SU9EY8R44
           "field5": isEnabled1 ? '1' : '0',
         }
       )
     });
       
    } catch(error) {
    console.log(error);
    }

  // const [isLoading, setLoading] = useState(true);
  // const [data, setData] = useState([]);
  // console.log(data);

  // useEffect(() => {

  //   fetch('https://thingspeak.com/channels/1548631/fields/5.json?results=1')
  //       .then((response) => response.json())
  //       .then((json) => setData(json))
  //       .catch((error) => console.error(error))
  //       .finally(() => setLoading(false));
  //   }, []);

  return (
    <>
      <StatusBar style="light" />
      <View>
          <Text style={styles.main_text}>Humidity</Text>
      </View>

      <WebView source={{ uri: 'https://thingspeak.com/channels/1548631/charts/5?bgcolor=%23ffffff&color=%23d62020&dynamic=true&results=60&type=line&update=15' }} 
      scalesPageToFit={true}
      style={{ width: '220%', marginBottom: 7}}
      />

      <View style={{flex: 0.2}}>
        <View style={{flexDirection:"row",  height: 40, }}>
          <View style={{flex:1}}>
            <Text style={styles.text}>Actual value</Text>
          </View>

          <View style={{flex:0.9}}>
            <FlatList
              data ={data.feeds}
              keyExtractor={({ field5 }, index) => field5}
              renderItem={({ item }) => (
                <Text style={styles.t_text}>{parseFloat(item.field5).toFixed(2)} %</Text>
              )}
            />
          </View>
        </View>
      </View>
      
      <View style={{flex: 0.2}}>
        <View style={{flexDirection:"row",  height: 50}}>
          <View style={{flex:1}}>
            <Text style={styles.text}>Manual Control</Text>
          </View>

          <View style={{flex:1}} style={styles.container}>
          <Switch
            trackColor={{ false: "#767577", true: "#3A6238" }}
            thumbColor={isEnabled ? "#7EB37A" : "#f4f3f4"}
            ios_backgroundColor="#3e3e3e"
            onValueChange={toggleSwitch}
            value={isEnabled}
          />
          </View>
        </View>
      </View>

      <View style={{flex: 0.2}}>
        <View style={{flexDirection:"row",  height: 55}}>
          <View style={{flex:1}}>
            <Text style={styles.text}>Actuator On/Off</Text>
          </View>

          <View style={{flex:1}} style={styles.container}>
          <Switch
            disabled={isEnabled ? false : true}
            trackColor={{ false: "#767577", true: "#3A6238" }}
            thumbColor={isEnabled1 ? "#7EB37A" : "#f4f3f4"}
            ios_backgroundColor="#3e3e3e"
            onValueChange={toggleSwitch1}
            value={isEnabled1}
          />
          </View>
        </View>
      </View>

      <WebView source={{ uri: 'https://thingspeak.com/channels/1633394/charts/2?bgcolor=%23ffffff&color=%23d62020&dynamic=true&results=60&title=Humidity+actuator&type=step' }} 
      scalesPageToFit={true}
      style={{ width: '220%', marginTop: 2,marginBottom: 5}}
      />

      <View style={{flex: 0.4}}>
        <View style={{flexDirection:"row", bottom: 5, left: 20}}>
          <View style={{flex:1}}>
            <IconButton icon="thermometer-lines" size={50} onPress={Temperature}/>
          </View>
          <View style={{flex:1}}>
            <IconButton icon="brightness-6" size={50} onPress={Light}/>
          </View>
          <View style={{flex:1}}>
           <IconButton icon="home" size={50} onPress={Home}/>
         </View>
        </View>
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  
  main_text: {
    textAlign: "center",
    fontFamily: "Cochin",
    fontWeight: "bold",
    color: '#3A6238',
    fontSize: 30,
    lineHeight: 40,

  },

  text: {
    textAlign: "center",
    fontFamily: "Cochin",
    fontWeight: "bold",
    color: '#3A6238',
    fontSize: 20,
    lineHeight: 30,

  },

  t_text: {
    textAlign: "center",
    fontFamily: "Cochin",
    color: '#3A6238',
    fontSize: 20,
    lineHeight: 30,
    backgroundColor: "#d9e8d8"
  },

  container: {
    flex: 1,
    alignItems: "center",
  },


});
